mapper.select:
select c.id, c.fullname, c.company, c.phone, c.mphone, u.name as owner, c.source, c.state
from tbl_clue c join tbl_user u on c.owner=u.id
c.fullname like '%' #{fullname} '%'
and c.company like '%' #{company} '%'
and c.phone like '%' #{phone} '%'
and c.mphone like '%' #{mphone} '%'
and c.source like '%' #{source} '%'
and u.name = #{owner}
and c.state = #{state}
order by c.fullname desc limit #{skipCount},#{pageSize}

mapper.delete:
delete from tbl_clue
where id in

    #{id}
