mapper.delete:
delete from tbl_contacts
where customerId in

    #{cid}

delete from tbl_contacts
where id in

    #{id}

mapper.select:
select c.id, c.fullname, c.customerId, cus.name as customerName, u.name as owner, c.source, c.birth
from tbl_contacts c join tbl_user u on c.owner=u.id left join tbl_customer cus on c.customerId=cus.id
c.fullname like '%' #{fullname} '%'
and cus.name like '%' #{customerName} '%'
and c.source =#{source}
and u.name like '%' #{owner} '%'
and c.birth = #{birth}
order by c.fullname desc limit #{skipCount},#{pageSize}
